import React, {Component} from 'react';
import PostService from "../../Services/PostService";
import Post from "./Post";

class AllPosts extends Component {
    state={posts:[],chosenPost:null};
    postService = new PostService();

    async componentDidMount() {
        let posts = await this.postService.getAllPosts();
        this.setState({posts});
    }

    onSelectedPost = (id)=>{
        let {posts} = this.state;
        let find = posts.find(value => value.id === id);
        this.setState({chosenPost: find});
    }

    render() {
let {posts,chosenPost} = this.state;
        return (
            <div>
                {chosenPost && posts.map(value => <Post key={value.id} item={value} chosenPost={chosenPost}/>)}
            </div>
        );
    }
}

export default AllPosts;
